import { Router } from "express";
import checkToken from "../../middlewares/checkToken.js";
import validation from "../../middlewares/validation.js";
import controller from "./controller.js";
const router = Router();
router.get("/directions", checkToken.user, controller.GET);
router.post("/directions", checkToken.admin, validation, controller.POST);
router.post("/directions/sciences", checkToken.user, controller.GETBYSCIENCES);
router.put("/directions/:directionId", checkToken.admin, validation, controller.PUT);
router.delete("/directions/:directionId", checkToken.admin, controller.DELETE);
export default router;
